mapper.select:
SELECT count(*)
FROM tbl_order


		player_phone = #{playerPhone}


		AND status IN

			#{status}

SELECT task_order_id,type,shop,order_no,money,fee,status,create_time
FROM tbl_order


		player_phone = #{playerPhone}


		AND status IN

			#{status}



ORDER BY create_time DESC
limit #{pageIndex},#{pageSize}
